		bool operator == (const String& s)const;	// 针对String对象
		bool operator == (const char* s)const;		// 针对char*
		bool operator != (const String& s)const;	// 针对String对象
		bool operator != (const char* s)const;		// 针对char*
		bool operator > (const String& s)const;	// 针对String对象
		bool operator > (const char* s)const;		// 针对char*
		bool operator < (const String& s)const;	// 针对String对象
		bool operator < (const char* s)const;		// 针对char*
